// ToDo Needs testing

class Storage {

  constructor() {
    this.store = {};
    // ideally want to create a fixed length queue as a separate class
    this.history = [];
  }

  // makeObj(key, val) {
  //   let obj = {};
  //   obj[key] = val;
  //   console.log('makeObj', obj);
  //   return obj;
  // };

  getAll() { return this.store; };

  // returns key and data
  getLastKeyValue() {
    const key = this.history[0];
    const val = this.load( key );
    return {key: key, value: val}
  };

  isEmpty() {
    return Object.keys(this.store).length === 0;
  }

  load(key) {
    this.history.push(key);
    return this.store[key];
  };

  save(key, val) {
    console.log('Key', key);
    console.log('Val', val);
    this.history.push(key);
    this.store[key] = val;
    return key;
  };
}

export default Storage;
