import * as d3 from 'd3';
import React, {Component} from 'react';
import ReactDOM from 'react-dom';


/**
 * This is a port of the d3-axis component.
 */
class GraphAxis extends Component {

  componentDidUpdate() { this.renderAxis(); }

  componentDidMount() { this.renderAxis(); }

  renderAxis() {
    var node = ReactDOM.findDOMNode(this);
    d3.select(node).call(this.props.axis);

  }

  render() {
    const translateX = "translate("+this.props.left+","+this.props.bottom+")";
    const translateY = "translate("+this.props.left+","+this.props.bottom+")";

    return (
        <g className="axis" transform={this.props.axisType==='x'?translateX:translateY} >
        </g>
    );
  }
}

GraphAxis.propTypes = {
  h:React.PropTypes.number,
  axis:React.PropTypes.func,
  axisType:React.PropTypes.oneOf(['x','y'])
};

export default GraphAxis;